import { getFirestore } from "firebase/firestore";
import { initializeApp } from "firebase/app";
import {getMessaging, getToken} from "firebase/compat/messaging";

// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyAPBK0pAUU7BZG471aUdDqSVvK8fpjDQgM",
  authDomain: "fir-cloud-messaging-5aa96.firebaseapp.com",
  projectId: "fir-cloud-messaging-5aa96",
  storageBucket: "fir-cloud-messaging-5aa96.appspot.com",
  messagingSenderId: "922589286071",
  appId: "1:922589286071:web:eb7cda93bfce92e4a20732"
};



const app = initializeApp(firebaseConfig);
const db = getFirestore();
export { db };

const messaging = getMessaging(app);

const { REACT_APP_VAPID_KEY } = process.env;
const publicKey = REACT_APP_VAPID_KEY;

export default getToken (messaging, { vapidKey: '<YOUR_PUBLIC_VAPID_KEY_HERE>' }).then((currentToken) => {
  if (currentToken) {
    // Send the token to your server and update the UI if necessary
    // ...
  } else {
    // Show permission request UI
    console.log('No registration token available. Request permission to generate one.');
    // ...
  }
}).catch((err) => {
  console.log('An error occurred while retrieving token. ', err);
  // ...
});





export const onMessageListener = () =>
  new Promise((resolve) => {
    messaging.onMessage((payload) => {
      resolve(payload);
    });
  });
