import { getFirestore } from "firebase/firestore";
import { initializeApp } from "firebase/app";
import "firebase/messaging";

// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyAPBK0pAUU7BZG471aUdDqSVvK8fpjDQgM",
  authDomain: "fir-cloud-messaging-5aa96.firebaseapp.com",
  projectId: "fir-cloud-messaging-5aa96",
  storageBucket: "fir-cloud-messaging-5aa96.appspot.com",
  messagingSenderId: "922589286071",
  appId: "1:922589286071:web:eb7cda93bfce92e4a20732"
};



const app = initializeApp(firebaseConfig);
const db = getFirestore();
export { db };

const messaginsg = messaging();

const { REACT_APP_VAPID_KEY } = process.env;
const publicKey = REACT_APP_VAPID_KEY;

export const getToken = async (setTokenFound) => {
  let currentToken = "";

  try {
    currentToken = await messaging.getToken({ vapidKey: publicKey });
    if (currentToken) {
      setTokenFound(true);
    } else {
      setTokenFound(false);
    }
  } catch (error) {
    console.log("An error occurred while retrieving token. ", error);
  }

  return currentToken;
};

export const onMessageListener = () =>
  new Promise((resolve) => {
    messaginsg.onMessage((payload) => {
      resolve(payload);
    });
  });
